mapper.update:
update project_ask set chat = concat(chat, #{my_chat}), last_chat = #{last_chat}, last_chat_date = now(),

		asker_read = #{read_at}
		 collector_read = #{read_at} 

where chatroom_seq = #{chatroom_seq};

update project_ask set  

		asker_read = #{read_at}
		 collector_read = #{read_at} 

where chatroom_seq = #{chatroom_seq};

mapper.select:
select chatroom_seq, a.project_seq, collector_seq,asker_seq,chat,last_chat,collector_read, asker_read,long_title,nickname, profile_img, member_url, url, main_images_url from project_ask a join project p on a.project_seq = p.project_seq
join member_info m on a.collector_seq = m.member_seq join member_info m on a.asker_seq = m.member_seq
where chatroom_seq = #{chatroom_seq}
